/*Table structure for table `carinfo` */

DROP TABLE IF EXISTS `carinfo`;

CREATE TABLE `carinfo` (
  `car_number` varchar(9) NOT NULL COMMENT '车牌号',
  `car_type` varchar(20) NOT NULL COMMENT '车类型',
  `car_length` float unsigned DEFAULT NULL COMMENT '车长度',
  `bear_weight` tinyint(3) unsigned DEFAULT NULL COMMENT '载重',
  `owner_id` int(10) unsigned DEFAULT NULL COMMENT '车拥有者id',
  `register_time` timestamp NULL DEFAULT NULL COMMENT '注册时间',
  `all_account` tinyint(3) unsigned DEFAULT NULL COMMENT '车的总数量',
  `using_account` tinyint(3) unsigned DEFAULT NULL COMMENT '使用中的数量',
  `start_station` varchar(20) DEFAULT NULL COMMENT '起发地',
  `destination_station` varchar(20) DEFAULT NULL COMMENT '目的地',
  `content` text COMMENT '车辆简介',
  PRIMARY KEY (`car_number`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

